mapper.select:
select *
from
    (select rownum cnt, A.*
    from
        (select *
        from RISK 
        where 1=1
        and prjno = #{prjno}
        AND riskname like '%'||#{schKeyword}||'%'
        order by riskpriority
        ) A
    )
where cnt between #{start} and #{end}

SELECT s.risk_STRATEGY FROM risk r, strategy s
WHERE r.RISKNO  = s.riskNO
AND s.riskno=#{riskno}
